import { ADD_FAVS, DELETE_FAVS, ORDER, FILTER,MOSTRAR} from "./types"
import  axios  from "axios"

export function addFavs(pro) {
    return async function (dispatch) {
        try{
            const data = await axios.post(`http://localhost:3001/rickandmorty/fav/add`, pro)
                dispatch({
                    type: ADD_FAVS,
                    payload: data.data,
                });
        }catch(error){
            console.log(error)
          }
      };
}
    

export function deleteFavs(id) {
    return async function (dispatch) {
        try{
            const deletdata = await axios.delete(`http://localhost:3001/rickandmorty/fav/delete/${id}`)
                dispatch({
                    type: DELETE_FAVS,
                    payload: deletdata.data
                })
        }catch(error){
            console.log(error)
        }
    };
}

export function filterCards(gender) {
    return {
        type: FILTER,
        payload: gender
    }
}

export function orderCards(orden){
    return {
        type: ORDER,
        payload: orden
    }
}

export function mostrarTodos(){
    return {
        type: MOSTRAR
    } 
}
