import React from "react";  
import {useState} from "react";
import {useParams} from "react-router-dom" 
import {useEffect} from "react";
import { NavLink}    from "react-router-dom";
import axios from "axios";
import styled from "styled-components";

const Detail = styled.div `
position: relative;
left: 20%;
width: 75%;
color: white;
font-size: 30px;
padding-top: 30%;
text-align: center;
`


export default function LocationDetail(props) {
const {id} = useParams()
const [location , setLocation] = useState({})
const [isLoading, setIsLoading] = useState(true);

async function fetchData() {  
    const data = await axios(`http://localhost:3001/all/findLocation/${id}`);
    setLocation(data.data);
    setIsLoading(false);
}
useEffect(() => {
    fetchData();
}, [id]);

if (isLoading) {
  return <div>Loading...</div>;
}

const { location: loc } = location;
const {character} = location;
console.log("====>",character)

if (!loc) {
  return <div>No location found</div>;
}

console.log(loc)
const { name, type, dimension } = loc;

return (
    <Detail>
        <NavLink to={"/Home"}>GoBack</NavLink>
        <h4>Location: {name}</h4>
        <h4>Type: {type}</h4>
        <h4>Dimension: {dimension}</h4>
        <h5>Native Borns: </h5>
        {}
    </Detail>
)
}
