import React from "react";  
import {useState} from "react";
import {useParams} from "react-router-dom" 
import {useEffect} from "react";
import { NavLink}    from "react-router-dom";
import axios from "axios";
import styled from "styled-components";

const Detail = styled.div `
position: relative;
left: 20%;
width: 75%;
color: white;
font-size: 30px;
padding-top: 30%;
text-align: center;
`


export default function LocationDetail(props) {
  const {id} = useParams()
  console.log("IDDDD", id)
  const [location , setLocation] = useState({})
  
  async function fetchData() {  
    const data = await axios(`http://localhost:3001/all/findLocation/${id}`);
    console.log("=====>",data.data)
    if (data.data.location) {
      setLocation(data.data);
    } else {
      window.alert("No hay locaciones con ese ID");
    }
  }
  useEffect(() => {
    fetchData();
  }, [id]);

  console.log("!!!!!!!!",location)
  return (
    <Detail>
        <NavLink to={"/Home"}>GoBack</NavLink>
      {location.location && location.location.name && (<h4>Location:   {location.location.name}</h4>)}
      {location.location && location.location.type && (<h4>Type:   {location.location.type}</h4>)}
      {location.location && location.location.dimension && (<h4>Dimension:   {location.location.dimension}</h4>)}
    
    </Detail>
  )}
