import { ADD_FAVS, DELETE_FAVS, ORDER, FILTER,MOSTRAR, GUARDARID} from "./types"
import  axios  from "axios"

export function addFavs(idC,idU) {
 console.log("estamos en addfav",idC,idU)
    return async function (dispatch) {
        try{
            const data = await axios.post(`http://localhost:3001/fav/add`, {idC,idU})
            console.log("data.data",data.data)
                dispatch({
                    type: ADD_FAVS,
                    payload: data.data,
                });
        }catch(error){
            console.log(error)
          }
      };
}
    

export function deleteFavs(idC,idU) {
    return async function (dispatch) {
        try{
            const deletdata = await axios.delete(`http://localhost:3001/fav/delete`,{idC,idU}) 
                dispatch({
                    type: DELETE_FAVS,
                    payload: deletdata
                });
        }catch(error){
            console.log(error)
        }
    };
}

export function filterCards(gender) {
    return {
        type: FILTER,
        payload: gender
    }
}

export function orderCards(orden){
    return {
        type: ORDER,
        payload: orden
    }
}

export function mostrarTodos(){
    return {
        type: MOSTRAR
    } 
}
export function guardaid(id){
    console.log("estamos en actions",id)
    return {
        type: GUARDARID,
        payload: id
    }
}
