import { ADD_FAVS, DELETE_FAVS, ORDER, FILTER,MOSTRAR, GUARDARID} from "./types"
import  axios  from "axios"

export function addFavs(props) {
    const {id,userID} = props;
    return async function (dispatch) {
        try{
           await axios.post(`http://localhost:3001/fav/add`, {id,userID})
                dispatch({
                    type: ADD_FAVS,
                    payload: props  
                });
        }catch(error){
            console.log(error)
          }
      };
}
    

export function deleteFavs(props) {
    const {id,userID} = props;
    console.log(id,userID,props)
    return async function (dispatch) {
        try{
            await axios.delete(`http://localhost:3001/fav/delete`, {id,userID}) 
                dispatch({
                    type: DELETE_FAVS,
                    payload: props.id
                });
        }catch(error){
            console.log(error)
        }
    };
}

export function filterCards(gender) {
    return {
        type: FILTER,
        payload: gender
    }
}

export function orderCards(orden){
    return {
        type: ORDER,
        payload: orden
    }
}

export function mostrarTodos(){
    return {
        type: MOSTRAR
    } 
}
