import React from "react";  
import {useState} from "react";
import {useParams} from "react-router-dom" 
import {useEffect} from "react";
import { NavLink}    from "react-router-dom";
import axios from "axios";
import styled from "styled-components";

const Detail = styled.div `
position: relative;
left: 20%;
width: 75%;
color: white;
font-size: 30px;
padding-top: 30%;
text-align: center;
`


export default function Details(props) {
  const {id} = useParams()
  const [character , setCharacter] = useState({})
  const pj = {}
  
  async function fetchData() {  
    const data = await axios(`http://localhost:3001/all/dbdata/${id}`);

    if (data.data.name) {
      pj = data.data;
    } else {
      window.alert("No hay personajes con ese ID");
    }
  }
  useEffect(() => {
    fetchData();
  }, [id]);
  console.log(pj)
 
    return (
      <Detail>
          <NavLink to={"/Home"}>GoBack</NavLink>
      <h2>{pj.name}</h2>
      <img src={pj.image} alt={pj.name}></img>
      <h3>Status:{pj.status}</h3>
      <h3>Specie:{pj.species}</h3>
      <h3>Gender:{pj.gender}</h3>
        <h3>Location:{pj.name}</h3>
        <h3>Location:{pj.name}</h3>
    
    
      </Detail >
    )
  
  
    
  
  
  
  
  
  
  }
